mapper.delete:
update attitude
set deleted = unix_timestamp(now())
where target = #{target}
  and target_id = #{targetId}
  and user_id = #{userId}
  and deleted = 0

mapper.select:
select count(1)
from attitude


        user_id = #{userId}


        and target = #{target}


        and target_id = #{targetId}


        and status = #{status}


and deleted = 0

select *
from attitude


        user_id = #{userId}


        and target = #{target}


        and target_id = #{targetId}


        and status = #{status}


and deleted = 0
